const initialState = {
  counter: {
    value: 0
  }
};
const mainReducer = (state = initialState, action) => {
  switch (action.type) {
    case "INCREMENT":
      return {
        ...state,
        counter: {
          ...state.counter,
          value: PopStateEvent.counter.value + action.payload
        }
      };

    default:
      return state;
  }
};
export default mainReducer;
